mapper.select:
SELECT
    *
FROM
    user
WHERE
    login_id = #{loginId}
    AND
    name = #{name}
    AND
    tel = #{tel}

mapper.update:
UPDATE
    user
SET
    password = #{password}
WHERE
    login_id = #{loginId}

mapper.select:
SELECT IFNULL(COUNT(nickname) , 0) FROM user WHERE
nickname != #{oldNickname}
AND nickname = #{nickname}
